import logo from './logo.svg';
import './App.css';
import Navbar from './components/Navbar/Navbar';
import {BrowserRouter,Routes,Route} from 'react-router-dom';
import { Shop } from './components/Pages/Shop';
import { ShopCategory } from './components/Pages/ShopCategory';
import { Product } from './components/Pages/Product';
import { Cart } from './components/Pages/Cart';
import { LoginSignup } from './components/Pages/LoginSignup';
import { Footer } from './components/Footer/Footer';
import ficban from './components/Assests/fic-ban.jpg';
import kid from './components/Assests/kid.jpg';
import nonfic from './components/Assests/nonfic.jpg'


function App() {
  return (
    <div>
      <BrowserRouter>
     <Navbar/>
     <Routes>
<Route path='/' element={<Shop/>}/>
<Route path='/fiction' element={<ShopCategory category="fiction"/>}/>
<Route path='/nonfic' element={<ShopCategory category="nonfic"/>}/>
<Route path='/novels' element={<ShopCategory category="novels"/>}/>
<Route path='/kids' element={<ShopCategory category="kids"/>}/>
<Route path="product" element={<Product/>}>
  <Route path=':productId' element={<Product/>}/>
</Route>
<Route path='/cart' element={<Cart/>}/>
<Route path='/login' element={<LoginSignup/>}/>
     </Routes>
     <Footer/>
     </BrowserRouter>
    </div>
  );
}

export default App;
